import React, { useState, KeyboardEvent } from 'react';

// Assuming these components are simple enough and just render their children.
const Drawer = ({ open, children }) => open ? <div className="drawer">{children}</div> : null;
const DrawerHeader = ({ children }) => <div className="drawer-header">{children}</div>;
const DrawerTitle = ({ children }) => <div className="drawer-title">{children}</div>;
const DrawerClose = ({ onClick }) => <button onClick={onClick}>Close</button>;
const DrawerContent = ({ children }) => <div className="drawer-content">{children}</div>;
const DrawerDescription = ({ children }) => <div className="drawer-description">{children}</div>;
const DrawerFooter = ({ children }) => <div className="drawer-footer">{children}</div>;
const DrawerTrigger = ({ onClick, children }) => <button onClick={onClick}>{children}</button>;
const Button = ({ onClick, children }) => <button onClick={onClick}>{children}</button>;

interface Message {
    user?: string;
    bot?: string;
}

const Chatbot = () => {
    const [isDrawerOpen, setIsDrawerOpen] = useState(false);
    const [messages, setMessages] = useState<Message[]>([]);

    const handleSendMessage = async (message: string) => {
        try {
            const response = await fetch('/chat/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ message }),
            });
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            // Update messages state with user message and bot response
            setMessages([...messages, { user: message }, { bot: data.response }]);
        } catch (error) {
            console.error('There has been a problem with your fetch operation:', error);
        }
    };

    const handleKeyDown = (event: KeyboardEvent<HTMLInputElement>) => {
        if (event.key === 'Enter') {
            const input = event.currentTarget;
            handleSendMessage(input.value);
            input.value = ''; // Clear input after sending
        }
    };

    return (
        <div>
            <DrawerTrigger onClick={() => setIsDrawerOpen(true)}>Open Chatbot</DrawerTrigger>
            <Drawer open={isDrawerOpen}>
                <DrawerHeader>
                    <DrawerTitle>Chatbot</DrawerTitle>
                    <DrawerClose onClick={() => setIsDrawerOpen(false)} />
                </DrawerHeader>
                <DrawerContent>
                    <DrawerDescription>This is your friendly chatbot!</DrawerDescription>
                    <DrawerFooter>
                        {/* Chat messages */}
                        {messages.map((msg, index) => (
                            <div key={index}>
                                {msg.user && <div>User: {msg.user}</div>}
                                {msg.bot && <div>Bot: {msg.bot}</div>}
                            </div>
                        ))}
                    </DrawerFooter>
                </DrawerContent>
            </Drawer>
            <input type="text" onKeyDown={handleKeyDown} />
            <Button onClick={() => setIsDrawerOpen(true)}>Open Chatbot</Button>
        </div>
    );
};

export default Chatbot;
